 create table customer ( id int auto_increment, name varchar(32), address varchar(100), primary key (id) ); create table orders ( id int auto_increment, date timestamp default now(), primary key (id) ); create table employee ( id int auto_increment, name varchar(32), address varchar(100), salary varchar(20), primary key (id) ); create table department ( name varchar(32), budget varchar(100), primary key (name) ); create table order_item ( amount varchar(32), discount varchar(100) ); create table product ( id int auto_increment, name varchar(32), descs varchar(100), price varchar(20), quantity varchar(20), primary key (id) ); create table supplier ( id int auto_increment, name varchar(32), address varchar(100), phone_number int(10), primary key (id) ); 